import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

const mockApiData = [
  {
    id: 1,
    label: "Art Collaborations",
    image: "data:image/jpeg;base64,",
    description: "Explore amazing art collaborations showcasing creativity and passion.",
    route: "/pages/ArtCollaborations",
  },
  {
    id: 2,
    label: "Employee Stories",
    image: "data:image/jpeg;base64,",
    description: "Real stories from employees about their journeys and experiences.",
    route: "/pages/EmployeeStories",
  },
  {
    id: 3,
    label: "Social Takeover",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRecyg0-GNUtx_gFLdt5l7sZ399mjO9X4BIeA&s",
    description: "Social takeover campaigns and community initiatives featured here.",
    route: "/pages/SocialTakeover",
  },
  {
    id: 4,
    label: "Podcast Series",
    image: "data:image/jpeg;base64,",
    description: "Listen to inspiring podcasts covering multiple topics and stories.",
    route: "/pages/PodcastSeries",
  },
];
const cardVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: i => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.15, type: "spring", stiffness: 100, damping: 15 },
  }),
  hover: {
    scale: 1.05,
    boxShadow: "0 8px 24px rgba(0, 0, 0, 0.3)",
    transition: { duration: 0.3 },
  },
};

function About() {
  const [pages, setPages] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");  // New: for search input
  const [sortAsc, setSortAsc] = useState(true);     // New: for toggling sort order

  useEffect(() => {
    setPages(mockApiData);
  }, []);

  // Filter and sort logic (JS feature added)
  const filteredPages = pages
    .filter(page =>
      page.label.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (a.label.toLowerCase() < b.label.toLowerCase()) return sortAsc ? -1 : 1;
      if (a.label.toLowerCase() > b.label.toLowerCase()) return sortAsc ? 1 : -1;
      return 0;
    });

  return (
    <div
      style={{
        maxWidth: 1710,
        margin: "0rem auto",
        padding: "1rem 2rem",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        backgroundColor: "#1A2331",
        minHeight: "100vh",
        color: "#ffffff",
        transition: "transform 0.3s ease",
      }}
    >
      {/* Animated Heading */}
      <motion.h1
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ type: "spring", stiffness: 120, damping: 15 }}
        style={{
          textAlign: "center",
          marginBottom: "3rem",
          fontSize: "3rem",
          fontWeight: "700",
          color: "#bcd1ff",
          userSelect: "none",
        }}
      >
        ðŸŒŸ Explore Our Pages
      </motion.h1>

      {/* Search Input and Sort Button - New JS Features */}
      <div
        style={{
          marginBottom: "2rem",
          display: "flex",
          justifyContent: "center",
          gap: "1rem",
          flexWrap: "wrap",
        }}
      >
        <input
          type="text"
          placeholder="Search pages..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          style={{
            padding: "0.5rem 1rem",
            borderRadius: 8,
            border: "none",
            color:"black",
            fontSize: 16,
            minWidth: 250,
            outline: "none",
          }}
        />
        <button
          onClick={() => setSortAsc(!sortAsc)}
          style={{
            padding: "0.5rem 1rem",
            borderRadius: 8,
            border: "none",
            cursor: "pointer",
            backgroundColor: "#60a5fa",
            color: "#1A2331",
            fontWeight: "600",
            fontSize: 16,
            userSelect: "none",
            transition: "background-color 0.3s ease",
          }}
          onMouseEnter={e => (e.target.style.backgroundColor = "#3b82f6")}
          onMouseLeave={e => (e.target.style.backgroundColor = "#60a5fa")}
        >
          Sort {sortAsc ? "â–²" : "â–¼"}
        </button>
      </div>

      {/* Display count of filtered pages */}
      <p style={{ textAlign: "center", marginBottom: "1rem", color: "#a0aec0" }}>
        Showing {filteredPages.length} out of {pages.length} pages
      </p>

      {/* Cards Grid */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
          gap: "2rem",
        }}
      >
        {filteredPages.map((page, index) => (
          <motion.div
            key={page.id}
            custom={index}
            variants={cardVariants}
            initial="hidden"
            animate="visible"
            whileHover="hover"
            style={{
              backgroundColor: "#2A3345", // darker card to match #1A2331
              borderRadius: 16,
              cursor: "pointer",
              padding: "1rem",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              transition: "transform 0.3s ease",
              color: "#ffffff",
            }}
          >
            <Link
              to={page.route}
              style={{
                textDecoration: "none",
                color: "inherit",
                display: "flex",
                flexDirection: "column",
                height: "100%",
              }}
            >
              <h3
                style={{
                  marginBottom: "1rem",
                  fontWeight: "600",
                  color: "#60a5fa", // soft blue heading
                  fontSize: "1.25rem",
                }}
              >
                {page.label}
              </h3>
              <img
                src={page.image}
                alt={page.label}
                style={{
                  width: "100%",
                  borderRadius: 12,
                  height: 150,
                  objectFit: "cover",
                  marginBottom: "1rem",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.05)",
                }}
              />
              <p
                style={{
                  fontSize: 15,
                  lineHeight: 1.5,
                  color: "#cbd5e1", // light gray text
                  flexGrow: 1,
                }}
              >
                {page.description}
              </p>
            </Link>
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default About;
