function TakeoverDetail() {
  const { id } = useParams();

  // id comes as string, convert to number for comparison
  const takeover = allTakeovers.find((t) => t.id === parseInt(id));

  if (!takeover) {
    return (
      <div style={{ backgroundColor: "#1A2331", color: "#BCD1FF", minHeight: "100vh", padding: "2rem" }}>
        <h2>Campaign Not Found</h2>
        <Link to="/" style={{ color: "#9abfff", textDecoration: "underline" }}>
          Go Back
        </Link>
      </div>
    );
  }

  return (
    <div style={{ backgroundColor: "#1A2331", color: "#BCD1FF", minHeight: "100vh", padding: "2rem", maxWidth: "1700px", margin: "auto" }}>
      <h1 style={{ fontSize: "2.5rem", marginBottom: "1rem" }}>{takeover.title}</h1>
      <img
        src={takeover.image}
        alt={takeover.title}
        style={{ width: "40%", borderRadius: "10px", marginBottom: "1rem" }}
      />
      <p style={{ fontSize: "1.2rem", marginBottom: "1rem" }}>{takeover.details}</p>
      <a
        href={takeover.url}
        target="_blank"
        rel="noopener noreferrer"
        style={{ color: "#9abfff", textDecoration: "underline" }}
      >
        Visit Campaign Site
      </a>
      <br />
      <br />
      <Link to="/pages/SocialTakeover" style={{ color: "#9abfff", textDecoration: "underline" }}>
        Back to Social Takeovers
      </Link>
    </div>
  );
}

